using AutoScrip.IPC;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace AutoScrip.Data;

class AutoHookPresets
{
    public static string ZorgorCondorPreset = $"AH4_";
    public static string ZorgorCondorCordialPreset = $"AH4_";
    public static string ZorgotCondorInverseCordialPreset = $"AH4_";
    public static string FleetingBrandPreset = $"AH4_";
    public static string FleetingBrandCordialPreset = $"AH4_";
    public static string FleetingInverseCordialPreset = $"AH4_";

    public static void SelectPreset()
    {
        if (C.SelectedScripColor == ScripColor.Orange)
        {
            if (C.UseCordial && C.ReverseCordialPrio)
                AutoHookIPC.CreateAndSelectAnonymousPreset(ZorgotCondorInverseCordialPreset);
            else if (C.UseCordial && !C.ReverseCordialPrio)
                AutoHookIPC.CreateAndSelectAnonymousPreset(ZorgorCondorCordialPreset);
            else
                AutoHookIPC.CreateAndSelectAnonymousPreset(ZorgorCondorPreset);
        }
        else
        {
            if (C.UseCordial && C.ReverseCordialPrio)
                AutoHookIPC.CreateAndSelectAnonymousPreset(FleetingInverseCordialPreset);
            else if (C.UseCordial && !C.ReverseCordialPrio)
                AutoHookIPC.CreateAndSelectAnonymousPreset(FleetingBrandCordialPreset);
            else
                AutoHookIPC.CreateAndSelectAnonymousPreset(FleetingBrandPreset);
        }
    }
}
